void cureSynthSetDryOutput(Operator *op)
{

	//Calculate Pitch bend control
	cureSynthApplyPBControl(op);

	//Calculate pitch
	float angular_temporary = (const_pitch_scalefactor * op->wav.freq) * op->wav.pitchScaleFactor * op->wav.modScaleFactor * op->wav.pitch_sweep_ScaleFactor * op->unisonScaleFactor;
 	op->wav.angular = (uint32_t)((1 << 16) * angular_temporary);

 	op->wav.angular_pcm = (uint32_t)((1 << 12) * angular_temporary * const_pcm_scalefactor);
 	//note angular_pcm contains integer as upper 20bit, and contains float as lower 12 bit.

	//Calculate RingModulator
	op->ringmod.angular = (uint32_t)(op->wav.angular * op->ringmod.multiply);
	op->ringmod.out = ( SineTable[ HIWORD(op->ringmod.pointer) ] * (op->ringmod.gain) + ( AUDIO_MAX_NUM * ( 127 - op->ringmod.gain )) ) >> 7;

	op->ringmod.pointer += op->ringmod.angular;
	op->ringmod.pointer = op->ringmod.pointer & ((SAMPLE_NUM << 16) - 1);

	//FMsynth test
//	uint32_t fm_pointer = op->wav.pointer + ((uint16_t)(op->ringmod.out - AUDIO_MIN_NUM) << 14);
//	fm_pointer = fm_pointer & ((SAMPLE_NUM << 16) - 1);

	//Calculate wavetable
	uint16_t pcm_buffer;

	if(PCM == op->wav.type){

		pcm_buffer = (pcm_ptr[op->wavenum])[HI20BIT(op->wav.pointer)] << 8;
		op->wav.out =  auConvU16ToI16(pcm_buffer);

		//if pointer doesn't run over the SAMPLE_NUM,
		op->wav.pointer += op->wav.angular_pcm;

		if(HI20BIT(op->wav.pointer) >= (pcm_len[op->wavenum] - 1)){
			op->wav.pointer = (pcm_len[op->wavenum] - 1) << 12;
		}

		//Calculate envelope(ADSR) for PCM
		_cureSynthApplyADSR_Pcm(op);

		//Calculate WG * EG *  Vol * Exp * Vel * RingMod and normalize it.
		op->out = ( op->wav.out * op->ringmod.out );
		op->out = __SMULWB( ( op->out ),  __SSAT(( ((uint8_t)op->env.out * op->velocity * op->out_gain) >> 7 ), 16 ) );
		op->out = op-> out >> (15 + OUTPUT_DIV_BIT);

	}else if(PCM_REV == op->wav.type){

		pcm_buffer = (pcm_ptr[op->wavenum])[(pcm_len[op->wavenum] - 1) - HI20BIT(op->wav.pointer)] << 8;
		op->wav.out =  auConvU16ToI16(pcm_buffer);

		//if pointer doesn't run over the SAMPLE_NUM,
		op->wav.pointer += op->wav.angular_pcm;

		if(HI20BIT(op->wav.pointer) >= (pcm_len[op->wavenum] - 1)){
			op->wav.pointer = (pcm_len[op->wavenum] - 1) << 12;
		}

		//Calculate envelope(ADSR) for PCM
		_cureSynthApplyADSR_Pcm(op);

		//Calculate WG * EG *  Vol * Exp * Vel * RingMod and normalize it.
		op->out = ( op->wav.out * op->ringmod.out );
		op->out = __SMULWB( ( op->out ),  __SSAT(( ((uint8_t)op->env.out * op->velocity * op->out_gain) >> 7 ), 16 ) );
		op->out = op-> out >> (15 + OUTPUT_DIV_BIT);

	}else{

		switch(op->wav.type){
				case SINE:

		#ifdef OVERSAMPLE_ON
					op->wav.out = cureSynthGenerateSineOS(op->wav.pointer, op->wav.angular);
		#elif defined LERP_ON
					op->wav.out = cureSynthGenerateSineLerp(op->wav.pointer);
		#else
					op->wav.out = cureSynthGetSine(op->wav.pointer);
		#endif
					break;

				case SQUARE:
		#ifdef OVERSAMPLE_ON
					op->wav.out = cureSynthGenerateSquareOS(op->wav.pointer, op->wav.angular);
		#elif defined LERP_ON
					op->wav.out = cureSynthGenerateSquareLerp(op->wav.pointer);
		#else
//					op->wav.out = cureSynthGetSquare(op->wav.pointer);
					op->wav.out = cureSynthGetSquareADD(op->wav.pointer, op->note_number);
		#endif
					break;

				case SAW:
		#ifdef OVERSAMPLE_ON
					op->wav.out = cureSynthGenerateSawOS(op->wav.pointer, op->wav.angular);
		#elif defined LERP_ON
					op->wav.out = cureSynthGenerateSawLerp(op->wav.pointer);
		#else

//					op->wav.out = cureSynthGetSaw(op->wav.pointer);
					op->wav.out = Saw_note_to_wavetable_ptr[op->note_number][HIWORD(op->wav.pointer)];
		#endif
					break;

				case TRIANGLE:
		#ifdef OVERSAMPLE_ON
					op->wav.out = cureSynthGenerateTriangleOS(op->wav.pointer, op->wav.angular, op->wav.triangle_peak_time);
		#elif defined LERP_ON
					op->wav.out = cureSynthGenerateTriangleLerp(op->wav.pointer, op->wav.triangle_peak_time);
		#else
					op->wav.out = cureSynthGetTriangle(op->wav.pointer, op->wav.triangle_peak_time);
		//			op->wav.out = Triangle_note_to_wavetable_ptr[op->note_number][HIWORD(op->wav.pointer)];
		#endif
					break;

				case WHITE_NOISE:
		//			op->wav.out =  (hrng.Instance->DR & 65535)  + AUDIO_MIN_NUM;
					op->wav.out =  (cureGenerateWhiteNoise() & 65535)  + AUDIO_MIN_NUM;
					break;

				case PINK_NOISE:
					op->wav.out =  (cureGeneratePinkNoise(op))  + AUDIO_MIN_NUM;
					break;

				default:
					break;
			}


			op->wav.pointer += op->wav.angular;

			//if pointer run over the SAMPLE_NUM,
			//then apply wave transformation.
			if( (~( ((uint32_t)SAMPLE_NUM << 16) - 1)) & op->wav.pointer ){
				op->wav.triangle_peak_time = op->wav.triangle_peak_time_buf;
			}

			op->wav.pointer = op->wav.pointer & ((SAMPLE_NUM << 16) - 1);


			//Calculate envelope(ADSR)
			_cureSynthApplyADSR(op);

			//Calculate WG * EG *  Vol * Exp * Vel * RingMod and normalize it.
			op->out = ( op->wav.out * op->ringmod.out );
			op->out = __SMULWB( ( op->out ),  __SSAT(( ((uint8_t)op->env.out * op->velocity * op->out_gain) >> 7 ), 16 ) );
			op->out = op-> out >> (15 + OUTPUT_DIV_BIT);

	}


	return;
}

void cureSynthApplyDCBlockAndAssignToChannel(Operator *op, uint8_t opnum)
{
	//Apply DC blocking
	float dcblock_out = (float)op->out - dcblocker[opnum].prev_in + (DCBLOCK_FACTOR * dcblocker[opnum].prev_out);
//	float dcblock_out = (float)op->out;	//no dc block
	dcblocker[opnum].prev_in = (float)op->out;
	dcblocker[opnum].prev_out = dcblock_out;

	//set dry output to au_out[] ordering by 16 ch.
	if( CHNUM > op->ch_dcblock ){
		au_out_32bit[op->ch_dcblock] += (int32_t)dcblock_out;	//use ch_dcblock
//		au_out[op->ch].mono.right += (int16_t)dcblock_out;	//use ch
//		au_out[op->ch].mono.right += (int16_t)op->out;// no dc block
	}
}

void cureSynthPanSmoothing()
{

	uint8_t pan, pan_ref;

	for(uint32_t i=0; i<CHNUM; i++){
		pan = synth_settings.pan[i];
		pan_ref = synth_settings.pan_ref[i];
		if(pan != pan_ref){
			synth_settings.pan[i] += ( (pan_ref - pan) / abs(pan_ref - pan) );
		}
	}

}


void cureSynthApplyVolume()
{

	//smoothing
	uint8_t exp, exp_ref;
	uint8_t vol, vol_ref;

	for(uint32_t i=0; i<CHNUM; i++){

		exp = synth_settings.expression[i];
		exp_ref = synth_settings.expression_ref[i];
		if(exp != exp_ref){
			synth_settings.expression[i] += ( (exp_ref - exp) / abs(exp_ref - exp) );
		}

		vol = synth_settings.volume[i];
		vol_ref = synth_settings.volume_ref[i];
		if(vol != vol_ref){
			synth_settings.volume[i] += ( (vol_ref - vol) / abs(vol_ref - vol) );
		}
	}


	//process
	uint32_t apsr_input = 0x00000000;

	__asm volatile ("MSR APSR_nzcvq, %0" : : "r" (apsr_input) : "memory");

	for(uint32_t i=0; i<CHNUM; i++){
		au_out_32bit[i] = (au_out_32bit[i] * synth_settings.volume[i] * synth_settings.expression[i]) >> (14+OUTPUT_VOLUME_DIV_BIT);
		au_out[i].mono.right = __SSAT(au_out_32bit[i], 16);
	}

	synth_is_clipped |= (__get_APSR() & 0x08000000);//get satulation Q flag
}



// Bi-Quad Filter
// referred to below
// http://aikelab.net/filter/
// http://vstcpp.wpblog.jp/?page_id=523
void cureSynthSetFilterParam(FilterType ftype, float fc, float Q, uint8_t ch)
{
	float omega, alpha, a0, a1, a2, b0, b1, b2;
	float coso;

	omega = 2.0f * M_PI * fc / SAMPLE_RATE;
	alpha = sin(omega) / (2.0f * Q);

	coso = cos(omega);

	if(LPF == ftype){

		a0 = 1.0f + alpha;
		a1 = -2.0f * coso;
		a2 = 1.0f - alpha;
		b0 = b2 = (1.0f - coso) / 2.0f;
		b1 = 1.0f - coso;

	}else{

		a0 = 1.0f + alpha;
		a1 = -2.0f * coso;
		a2 = 1.0f - alpha;
		b0 = b2 = (1.0f + coso) / 2.0f;
		b1 = -(1.0f - coso);
	}

	flt_settings[ch].b0a0 = (b0 / a0);
	flt_settings[ch].b1a0 = (b1 / a0);
	flt_settings[ch].b2a0 = (b2 / a0);
	flt_settings[ch].a1a0 = (a1 / a0);
	flt_settings[ch].a2a0 = (a2 / a0);

}

// Bi-Quad Filter
// referred to below
// http://aikelab.net/filter/
// http://vstcpp.wpblog.jp/?page_id=523
#define FILT_BIQUAD_SAFETY_COEF (0.99f)
void cureSynthApplyFilter()
{
	uint32_t i;
	int16_t input, output;


	for(i=0; i<CHNUM; i++){

		input = au_out[i].mono.right;

		output = __SSAT(
					( flt_settings[i].b0a0 * (float)input
					+ flt_settings[i].b1a0 * (float)flt_settings[i].in1
					+ flt_settings[i].b2a0 * (float)flt_settings[i].in2
					- flt_settings[i].a1a0 * (float)flt_settings[i].out1
					- flt_settings[i].a2a0 * (float)flt_settings[i].out2 )
				, 16);

		flt_settings[i].in2 = flt_settings[i].in1;
		flt_settings[i].in1 = input;

		flt_settings[i].out2 = flt_settings[i].out1;
		flt_settings[i].out1 = output;

		au_out[i].mono.right = output;
	}
}

void cureSynthApplyDistortion()
{
	uint32_t i;
	int16_t input, output;
	uint8_t input_reduced_bit;

	for(i=0; i<CHNUM; i++){

		switch(synth_settings.dst_mode[i]){
			case DIST_HARD:
				input = __SSAT( (int32_t)(au_out[i].mono.right * synth_settings.dst_level[i]) >> DIST_LEVEL_DIV_BIT  , 16 );
				input_reduced_bit = (uint8_t)( (uint32_t)( input - AUDIO_MIN_NUM) >>8);
				output = DistTable_hard[input_reduced_bit];
				output = __SSAT((output * synth_settings.dst_gain[i]) >> OUTPUT_DIST_DIV_BIT , 16);
				au_out[i].mono.right = output;
				break;

			case DIST_SOFT:
				input = __SSAT( (int32_t)(au_out[i].mono.right * synth_settings.dst_level[i]) >> DIST_LEVEL_DIV_BIT  , 16 );
				input_reduced_bit = (uint8_t)( (uint32_t)( input - AUDIO_MIN_NUM) >>8);
				output = DistTable_soft[input_reduced_bit];
				output = __SSAT((output * synth_settings.dst_gain[i]) >> OUTPUT_DIST_DIV_BIT , 16);
				au_out[i].mono.right = output;
				break;

			case DIST_ASSYM:
				input = __SSAT( (int32_t)(au_out[i].mono.right * synth_settings.dst_level[i]) >> DIST_LEVEL_DIV_BIT  , 16 );
				input_reduced_bit = (uint8_t)( (uint32_t)( input - AUDIO_MIN_NUM) >>8);
				output = DistTable_assym[input_reduced_bit];
				output = __SSAT((output * synth_settings.dst_gain[i]) >> OUTPUT_DIST_DIV_BIT , 16);
				au_out[i].mono.right = output;
				break;

			default:
				break;
		}

	}
}

//Apply Pan and RCD(Reverb, Chorus, Delay)
void cureSynthApplyPRCD(int16_t output[2])
{
	uint32_t i;

	Audio buf = {0}, dly_send = {0}, cho_send = {0}, rev_send = {0}, out={0};

	//Pan
	cureSynthPanSmoothing();
	for(i=0; i<CHNUM; i++){
		au_out[i].mono.left = ( au_out[i].mono.right * PanTable[ synth_settings.pan[i] ] ) >> 7;
		au_out[i].mono.right = ( au_out[i].mono.right * PanTable[128 - synth_settings.pan[i] ] ) >> 7;
		out.stereo_out = __QADD16(out.stereo_out, au_out[i].stereo_out);
	}

#ifdef EFX_DELAY_ON
	//Delay
	for(i=0; i<CHNUM; i++){
		buf.mono.left = (au_out[i].mono.left * synth_settings.delay_level[i]) >> 7;
		buf.mono.right = (au_out[i].mono.right * synth_settings.delay_level[i]) >> 7;
		dly_send.stereo_out = __QADD16(dly_send.stereo_out, buf.stereo_out);
	}

	efxApplyDelay(&dly_send);
#endif

#ifdef EFX_CHORUS_ON
	//Chorus
	for(i=0; i<CHNUM; i++){
		buf.mono.left =  (au_out[i].mono.left * synth_settings.chorus_level[i]) >> 7;
		buf.mono.right =  (au_out[i].mono.right * synth_settings.chorus_level[i]) >> 7;
		cho_send.stereo_out = __QADD16(cho_send.stereo_out, buf.stereo_out);
	}

	efxApplyChorus(&cho_send);
#endif

#ifdef EFX_REVERB_ON
	//Reverb
	for(i=0; i<CHNUM; i++){
		buf.mono.left =  (au_out[i].mono.left * synth_settings.reverb_level[i]) >> 7;
		buf.mono.right =  (au_out[i].mono.right * synth_settings.reverb_level[i]) >> 7;
		rev_send.stereo_out = __QADD16(rev_send.stereo_out, buf.stereo_out);
	}

	efxApplyReverb(&rev_send);
#endif

//	//Mix
	out.stereo_out = __QADD16( out.stereo_out, dly_send.stereo_out);
	out.stereo_out = __QADD16( out.stereo_out, cho_send.stereo_out);
	*(uint32_t *)output = __QADD16( out.stereo_out, rev_send.stereo_out);

	//todo:Chorus bypass test
//	*(uint32_t *)output = cho_send.stereo_out;

	//Chorus only
//	*(uint32_t *)output = __QADD16( out.stereo_out, cho_send.stereo_out);

	//All effect Bypass
//	*(uint32_t *)output = out.stereo_out;

}

