void cureSynthApplyPeakDetect(int16_t output[2])
{
	int16_t output_abs[2];
	float peak_calc[2];

	for(uint32_t i=0; i<2; i++){
		peak_calc[i] = (PEAKDETECT_FACTOR * synth_peak_detect.prev_out[i]) + (1.0f - PEAKDETECT_FACTOR) * abs(output[i]);
		synth_peak_detect.prev_out[i] = peak_calc[i];
	}
}

void cureSynthGetOutput(int16_t output[OUTPUT_CH_NUM])
{
	uint32_t i;

	//Initialize
	for(i=0; i<CHNUM; i++){
		au_out[i].stereo_out = 0U;
		au_out_32bit[i] = 0;
	}
	synth_voice_number_status.spare_voice_num_counter = 0;

	//Setting Modulation Parameter
	cureSynthApplyModulation();

	//Set dry output
	for(i=0; i<OPNUM; i++){
		cureSynthSetDryOutput(&au_operator[i]);
		cureSynthApplyDCBlockAndAssignToChannel(&au_operator[i], i);

	}

	//Apply Volume and Expression
	cureSynthApplyVolume();

	//Apply Distortion
	cureSynthApplyDistortion();

	//Apply Bi-Quad Filter.
	cureSynthApplyFilter();

	//Pan, Reverb, Chorus, Delay
	cureSynthApplyPRCD(output);

	cureSynthApplyMasterVolume(output);

	//Set Master OUTPUT
	output[0] = output[0] >> MASTER_OUTPUT_DIV_BIT;
	output[1] = output[1] >> MASTER_OUTPUT_DIV_BIT;

	//peak detection
	cureSynthApplyPeakDetect(output);

	//calc voice number
	synth_voice_number_status.voice_num = OPNUM - synth_voice_number_status.spare_voice_num_counter;


	return;
}

void cureSynthGenerateTestSignalSquare(int16_t output[OUTPUT_CH_NUM])
{
	static uint8_t cnt = 0;

	if(cnt < 24){
		output[0] = INT16_MAX;
		output[1] = INT16_MAX;
	}else if(cnt < 48){
		output[0] = INT16_MIN;
		output[1] = INT16_MIN;
	}

	cnt++;
	if(cnt >=48){
		cnt = 0;
	}

	//peak detection
	cureSynthApplyPeakDetect(output);

}

void cureSynthSetNoteON(Operator *op, uint8_t sc, uint8_t vel, uint8_t midich)
{
	op->wav.freqbuf = ScaleFreqTable[sc];
	op->velocity = vel;
	op->env.stat = ADSR_START;
	op->env.idx = 0;
	op->ringmod.pointer = op->wav.pointer = 0.0;
	op->stat = NOTE_ON;

	op->ch = op->ch_dcblock = midich;	//While Operator is making sound (Note-on to ADSR_END), op->ch and ch_dcblock is available.

	op->unisonScale = sc; //default:-1, unison-on:scale number;

	op->note_number = sc;	//note number: continue to next note-on.
}

//scale_and_realscale
//upper byte is scale number by midi message.
//lower byte is scale converted by patchlist.
//the number of arguments in ARM is 4, so pack scale and realscale to 16bit uint.
void cureSynthSetNoteON_DRUM(Operator *op, uint16_t scale_and_realscale, uint8_t vel, uint8_t midich)
{
	op->wav.freqbuf = ScaleFreqTable[(scale_and_realscale & 0x00FF)];
	op->velocity = vel;
	op->env.stat = ADSR_START;
	op->env.idx = 0;
	op->ringmod.pointer = op->wav.pointer = 0.0;
	op->stat = NOTE_ON;

	op->ch = op->ch_dcblock = midich;	//While Operator is making sound (Note-on to ADSR_END), op->ch is available.
	op->unisonScale = (scale_and_realscale & 0xFF00) >> 8; //default:-1, unison-on:scale number;

	op->note_number = (scale_and_realscale & 0xFF00) >> 8;	//note number: the value continue to next note-on.
}

void cureSynthSetNoteOFF(Operator *op)
{
	if( NOTE_MELODY == op->ntype){
		op->env.stat = RELEASE;
		op->env.idx = 0;
		op->stat = NOTE_OFF;
		op->unisonScale = -1;
	}else{// if op->ntype == NOTE_DRUM, disable release.

		op->stat = NOTE_OFF;
		op->unisonScale = -1;
	}
}

//exclusive drum note off only.
void cureSynthSetNoteOFFImmediately(Operator *op)
{
		op->env.stat = ADSR_END;
		op->env.idx = 0;
		op->stat = NOTE_OFF;
		op->unisonScale = -1;
}

void cureSynthOperatorApplyPitchBendAndTuning(Operator *op, float tuning_scalefactor)
{
	int32_t bendOctave = 0, bendAfterDecimal = 0, tmp = 0;
	float scaleFactor = 1.0;

	uint8_t i;

	//Calculate PitchBend
	if(op->wav.pitch >=8192){
		tmp = ( (op->wav.pitch - 8192) * op->wav.pitchbendsensitivity * 64) / 8192;
		bendOctave = tmp / PITCHTABLE_NUM;
		bendAfterDecimal = tmp - PITCHTABLE_NUM * bendOctave;
		for(i=0; i<bendOctave; i++){
			scaleFactor *= 2.0;
		}
		scaleFactor *= PitchbendTable[bendAfterDecimal];

	}else{
		tmp = ( (8192 - op->wav.pitch) * op->wav.pitchbendsensitivity * 64) / 8192;
		bendOctave = tmp / PITCHTABLE_NUM;
		bendAfterDecimal = tmp - PITCHTABLE_NUM * bendOctave;
		for(i=0; i<bendOctave; i++){
			scaleFactor /= 2.0;
		}
		scaleFactor /= PitchbendTable[bendAfterDecimal];

	}
	op->wav.pitchScaleFactor = scaleFactor * tuning_scalefactor;
}

void cureSynthSetFilter(uint8_t ch)
{

	float fc,q;

	uint8_t ct, rs;

	ct = synth_settings.cutoff[ch];
	rs = synth_settings.resonance[ch];

	if( 64 >= ct){
		fc = FLT_FREQ_MIN + (float)ct * (FLT_FREQ_MID - FLT_FREQ_MIN) / 64.0f;
	}else{
		fc = FLT_FREQ_MID + (float)(ct - 64) * (FLT_FREQ_MAX - FLT_FREQ_MID) / 63.0f;
	}

	if( 64 >= rs){
		q = FLT_QFACT_MIN + ( (float)rs * (FLT_QFACT_MID - FLT_QFACT_MIN) ) / 64.0f;
	}else{
		q = FLT_QFACT_MID + ( (float)(rs - 64) * (FLT_QFACT_MAX - FLT_QFACT_MID) ) / 63.0f;
	}

	cureSynthSetFilterParam(synth_settings.ftype[ch], fc, q, ch);

}

void cureSynthAllSoundOff(uint8_t ch)
{
	uint32_t i;

	for(i=0; i<OPNUM; i++){
		if( ch == au_operator[i].ch){
			cureSynthSetNoteOFFImmediately(&au_operator[i]);
//			cureSynthOperatorInit(&au_operator[i]);
		}
	}
}


